tinymce.PluginManager.add('align',function(editor){editor.addButton('align',{type:'splitbutton',text: '',icon:'alignleft',onclick:function(e){tinyMCE.execCommand(this.value);},menu:[{icon:'alignleft',text:'Align Left',onclick:function(){tinyMCE.execCommand('JustifyLeft');this.parent().parent().icon('alignleft');this.parent().parent().value='JustifyLeft';}},{icon:'alignright',text:'Align Right',onclick:function(){tinyMCE.execCommand('JustifyRight');this.parent().parent().icon('alignright');this.parent().parent().value='JustifyRight';}},{icon:'aligncenter',text:'Align Center',onclick:function(){tinyMCE.execCommand('JustifyCenter');this.parent().parent().icon('aligncenter');this.parent().parent().value ='JustifyCenter';}},{icon:'alignjustify',text:'Justify',onclick: function(){tinyMCE.execCommand('JustifyFull');this.parent().parent().icon('alignjustify');this.parent().parent().value='JustifyFull';}}],onPostRender:function(){this.value='JustifyLeft';}});});